(162, '_site_transient_wp_plugin_dependencies_plugin_data', 'a:2:{s:9:\"elementor\";a:35:{s:4:\"name\";s:63:\"Elementor Website Builder &#8211; More than Just a Page Builder\";s:4:\"slug\";s:9:\"elementor\";s:7:\"version\";s:7:\"3.25.11\";s:6:\"author\";s:115:\"<a href=\"https://elementor.com/?utm_source=wp-plugins&utm_campaign=author-uri&utm_medium=wp-dash\">Elementor.com</a>\";s:14:\"author_profile\";s:40:\"https://profiles.wordpress.org/elemntor/\";s:12:\"contributors\";a:1:{s:8:\"elemntor\";a:3:{s:7:\"profile\";s:40:\"https://profiles.wordpress.org/elemntor/\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/5da4d5137e6bbfaac62bc33164a10887?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:9:\"Elementor\";}}s:8:\"requires\";s:3:\"6.3\";s:6:\"tested\";s:5:\"6.7.1\";s:12:\"requires_php\";s:3:\"7.4\";s:16:\"requires_plugins\";a:0:{}s:6:\"rating\";i:92;s:7:\"ratings\";a:5:{i:5;i:5964;i:4;i:212;i:3;i:99;i:2;i:99;i:1;i:592;}s:11:\"num_ratings\";i:6966;s:11:\"support_url\";s:47:\"https://wordpress.org/support/plugin/elementor/\";s:15:\"support_threads\";i:207;s:24:\"support_threads_resolved\";i:94;s:15:\"active_installs\";i:10000000;s:12:\"last_updated\";s:21:\"2024-12-10 1:55pm GMT\";s:5:\"added\";s:10:\"2016-05-30\";s:8:\"homepage\";s:87:\"https://elementor.com/?utm_source=wp-plugins&utm_campaign=plugin-uri&utm_medium=wp-dash\";s:8:\"sections\";a:6:{s:11:\"description\";s:25486:\"<p><iframe loading=\"lazy\" title=\"Elementor - The Leading Platform for Web Creators\" width=\"750\" height=\"422\" src=\"https://www.youtube.com/embed/le72grP_Q6k?feature=oembed\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" referrerpolicy=\"strict-origin-when-cross-origin\" allowfullscreen></iframe></p>\n<h4>THE #1 NO CODE DRAG &amp; DROP WORDPRESS WEBSITE BUILDER POWERING OVER 17M WEBSITES WORLDWIDE, NOW WITH AI.</h4>\n<p>Elementor, the leading WordPress website creation platform, empowers you to buil