(50, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-12-12 14:14:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-12-12 14:14:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-12-12 14:14:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-12-12 14:14:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-12-12 14:14:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2024-12-12 14:14:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'usps-mail-service-to-canada-suspended-dec-2024', 'update', 'en_US', 'Please note: USPS Mail Service to Canada Suspended', 'The United States Postal Service has temporarily suspended mail service to Canada due to the ongoing Canadian postal workers\' strike. USPS is asking users to refrain from mailing items to Canada at this time.', '{}', 'pending', 'woocommerce.com', '2024-12-12 14:14:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'wc-admin-add-first-product-note', 'email', 'en_US', 'Ajouter votre premier produit', '{greetings}<br /><br />Félicitations ! Vous avez créé votre boutique WooCommerce ! Il est maintenant temps d’ajouter votre premier produit et de commencer à le vendre.<br /><br />Trois méthodes permettent d’ajouter des produits : vous pouvez <strong>créer les produits manuellement, les importer d’une traite par l’intermédiaire d’un fichier CSV</strong>, ou <strong>les migrer depuis un autre service</strong>.<br /><br /><a href=\"https://woocommerce.com/document/managing-products/?utm_source=help_panel&amp;utm_medium=product\">Explorez notre documentation</a> pour plus d’informations ou bien lancez-vous !', '{\"role\":\"administrator\"}', 'unactioned', 'woocommerce-admin', '2024-12-15 10:42:18', NULL, 0, 'plain', 'https://localhost/wordpress/wp-content/plugins/woocommerce/images/admin_notes/dashboard-widget-setup.png', 0, 0, 'info'),
(58, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Vous cherchez un thème ?', 'Découvrez les thèmes compatibles avec WooCommerce et choisissez celui qui correspond le mieux à l’image de votre marque et à vos besoins.', '{}', 'unactioned', 'woocommerce-admin', '2024-12-15 10:42:18', NULL, 0, 'plain', '', 0, 0, 'info'),
